.switch {
    position: relative;
    display: inline-block;
    width: 40px;
    height: 25px;
    cursor: pointer;
    background-color: #fff;
    border-radius: 999px
}

.slider,
.slider:before {
    position: absolute;
    background-color: #c2c2c2;
    bottom: 0
}

.brandingImage,
.switch input {
    display: none
}

.slider {
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    -webkit-transition: .2s;
    transition: .2s
}

.slider:before {
    content: "";
    height: 23px;
    width: 23px;
    left: 1px;
    background: #fff;
    -webkit-transition: .2s;
    transition: .2s;
    box-shadow: 0 1px 3px 1px rgba(0, 0, 0, .2), 0 1px 1px 0 rgba(0, 0, 0, .14), 0 2px 1px -1px rgba(0, 0, 0, .12)
}

input:checked + .slider {
    background-color: #36B37E;
}

input:focus + .slider {
    box-shadow: 0 0 1px #36B37E;
}

input:checked + .slider:before {
    -webkit-transform: translateX(14px);
    -ms-transform: translateX(14px);
    transform: translateX(14px)
}

.slider.round {
    border-radius: 14px
}

.slider.round:before,
.userImgWhatsUp {
    border-radius: 50%
}

