function pageprocessorPreferences3(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $rootScope.back = function() {
    $location.path("/app/BlueLightEmergency/BlueLightEmergency");
  };

  var CheckSettings = function() {
    try {

      if ($.jStorage.get("SettingsData") == null) {
        var Settings = {};
        Settings.alertText = "Need Help."
        Settings.medicalInfo = false,
          Settings.additionalInfo = false,
          //Settings.defaultGuardian = true,
          Settings.searchRadius = '10',
          Settings.TrackMeInterval = '15'
        $.jStorage.set("SettingsData", Settings);

      }
      var data = $.jStorage.get('SettingsData');

      $("#AlertText").val(data.alertText);

      $scope.medicalInfo = data.medicalInfo;

      $scope.additionalInfo = data.additionalInfo;
      //$scope.defaultGuardian=data.defaultGuardian;

      $("#searchRadius").val(data.searchRadius);

      $("#TrackMeInterval").val(data.TrackMeInterval);
    } catch (e) {
      alert(e)
    }
  }

  $scope.SaveSettingsInfo = function() {
    try {
      var Settings = {};

      Settings.alertText = $("#AlertText").val();
      Settings.medicalInfo = $scope.medicalInfo;
      Settings.additionalInfo = $scope.additionalInfo;
      //Settings.defaultGuardian = $scope.defaultGuardian;
      Settings.searchRadius = $("#searchRadius").val();
      Settings.TrackMeInterval = $("#TrackMeInterval").val();

      $.jStorage.set("SettingsData", Settings);
      if (window.device) {
        navigator.notification.alert("Settings information is saved successfully.", null, 'Alert', 'ok');
        $location.path("/app/BlueLightEmergency/BlueLightEmergency");
      } else {
        alert("Settings information is saved successfully.");
      }
    } catch (e) {
      alert(e)
    }
  }

  CheckSettings();
}

function pageprocessorDirectory(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $building) {
  $scope.resultfound = true;
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $('#srch').on('submit', function(event) {
    event.preventDefault();
    $scope.search();
    $scope.$digest();
    $("#srchbtn").focus();
    return false;
  });
  $scope.fetched = false;
  $scope.filterresult = function() {

    var searchTxt = $("#Directorytxt").val();
    $rootScope.Directoryval = searchTxt;
    var results = [];
    var patt = new RegExp(searchTxt, "i");
    var filterParams = pagedef.searchfields.split(",");

    $.each($scope.DirectoryList.SWTJC_DIRECTORY, function(index, val) {
      var found = false;

      if (($scope.seldept == '' || $scope.seldept == null) && ($scope.Directoryval == '' || $scope.Directoryval == null) && ($scope.loc == '' || $scope.loc == null)) {

        if (window.device) {
          navigator.notification.alert("Select atleast one Field");
          return false;
        } else {
          alert("Select atleast one Field");
          return false;

        }
      }


      if (($scope.seldept == '' || $scope.seldept == null) && ($scope.loc == '' || $scope.loc == null)){

        for (var i = 0; i < filterParams.length; i++) {
          if (val["" + filterParams[i] + ""].match(patt)) {
            found = true;
            break;
          }
        }
        if (found) {
          results.push(val);
        }
      } 

      else if (($scope.seldept == val.department) && ($scope.Directoryval == '' || $scope.Directoryval == null) && ($scope.loc == '' || $scope.loc == null)){

        for (var i = 0; i < filterParams.length; i++) {
          if (val["" + filterParams[i] + ""].match(patt)) {
            found = true;
            break;
          }
        }
        if (found) {
          results.push(val);
        }
      } 

      else if (($scope.seldept == val.department) && ($scope.loc == '' || $scope.loc == null)){

        for (var i = 0; i < filterParams.length; i++) {
          if (val["" + filterParams[i] + ""].match(patt)) {
            found = true;
            break;
          }
        }
        if (found) {
          results.push(val);
        }
      }


      else if (($scope.seldept == '' || $scope.seldept == null) && ($scope.Directoryval == '' || $scope.Directoryval == null) && ($scope.loc == val.campus)) {

        for (var i = 0; i < filterParams.length; i++) {
          if (val["" + filterParams[i] + ""].match(patt)) {
            found = true;
            break;
          }
        }
        if (found) {
          results.push(val);
        }
      }

      else if (($scope.seldept == '' || $scope.seldept == null) && ($scope.loc == val.campus)) {

        for (var i = 0; i < filterParams.length; i++) {
          if (val["" + filterParams[i] + ""].match(patt)) {
            found = true;
            break;
          }
        }
        if (found) {
          results.push(val);
        }
      }

      else if (($scope.seldept == val.department) && ($scope.loc == val.campus)) {

        for (var i = 0; i < filterParams.length; i++) {
          if (val["" + filterParams[i] + ""].match(patt)) {
            found = true;
            break;
          }
        }
        if (found) {
          results.push(val);
        }
      }

      else if (($scope.seldept == val.department) && ($scope.loc == val.campus) && ($scope.Directoryval == '' || $scope.Directoryval == null)) {

        for (var i = 0; i < filterParams.length; i++) {
          if (val["" + filterParams[i] + ""].match(patt)) {
            found = true;
            break;
          }
        }
        if (found) {
          results.push(val);
        }
      }

    });
    $rootScope.DirectoryResult = results;
    if (results.length > 0) {
      $scope.resultfound = true;
    } else {
      $scope.resultfound = false;
    }
  };


  $scope.search = function() {
    if ($scope.fetched == false) {
      $.blockUI();
      var url = pagedef.url;
      if (window.device) {} else {
        url = "/websimulator/xml?url=" + pagedef.url;
      }
      var xhr = $.ajax({
        url: url,
        dataType: "xml",
        async: true,
        data: {},
        success: function(resp) {
          try {
            var results = $.xml2json(resp);
            $scope.fetched = true;
            $scope.DirectoryList = results;

            console.log(JSON.stringify(results));


            /*Department dropdown-start*/
            var tempdept = "";
            var depts = [];
            $.each(results.SWTJC_DIRECTORY, function(i, v) {
              if (tempdept.indexOf(v.department) == -1) {
                depts.push({
                  "DEPT_CODE": v.department,
                  "DEPT_NAME": v.department
                });
                tempdept += v.department;
              }
            });
            depts.sort(function(a, b) {
              if (a.DEPT_CODE < b.DEPT_CODE) {
                return -1;
              } else if (a.DEPT_CODE > b.DEPT_CODE) {
                return 1;
              }

            });

            $scope.depts = depts;
            $rootScope.depts = depts;

            /*Department dropdown-end*/


            /*buildings dropdown-start*/
            var temploc = "";
            var buildings = [];
            $.each(results.SWTJC_DIRECTORY, function(i, v) {
              if (temploc.indexOf(v.campus) == -1) {
                buildings.push({
                  "CAMPUS_CODE": v.campus
                });
                temploc += v.campus;
              }
            });
            buildings.sort(function(a, b) {
              if (a.CAMPUS_CODE < b.CAMPUS_CODE) {
                return -1;
              } else if (a.CAMPUS_CODE > b.CAMPUS_CODE) {
                return 1;
              }

            });

            $scope.buildings = buildings;
            $rootScope.buildings = buildings;

            /*buildings dropdown-end*/




            //$scope.filterresult();
            $scope.$apply();
            $.unblockUI();
          } catch (E) {
            alert("Exception : " + E);
          }
        },
        error: function(e) {
          alert("Error : " + e);
          $.unblockUI();
          apprise("Error occured. Please try again later.", {
            "verify": false,
            "textYes": "Ok"
          }, function(r) {
            $window.history.back();
          });
        }
      });
    } else {
      $scope.filterresult();
    }
  }
  $scope.search();
}

