function pageprocessorProfile2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.NoProfile = true;
  var tenantid = $rootScope.metadata.tenantid;
  var mobappid = $rootScope.metadata.mobappid;
  var featureid = "87722";

  $rootScope.back = function() {
    $location.path("/app/BlueLightEmergency/BlueLightEmergency");
  };

  $scope.PI = true;
  $(".tabsContent1").hide();

  $("#PIContent").show();

  $scope.showHide = function(show) {
    $scope.PI = false;
    $scope.MI = false;
    $scope.AI = false;
    if (show == 'MI') {
      $scope.MI = true;
    } else if (show == 'AI') {
      $scope.AI = true;
    } else {
      $scope.PI = true;
    }

    $(".tabsContent1").hide();
    $("#" + show + "Content").show();
  }
  var checkPersonalInfo = function() {
    try {

      if ($.jStorage.get("PersonalInfo") == null) {
        $scope.NoProfile = true;
      } else {
        $scope.NoProfile = false;
        var PI = $.jStorage.get("PersonalInfo");
        var phn = PI.PhNum;
        var id = PI.id;
        var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=UserProfiles";
        var proxyMethod = "POST";

        var data = {
          "_id": id,
          "phone": phn
        };
        var proxyData = data;
        $.blockUI();
        $http({
          method: proxyMethod,
          url: url,
          data: proxyData
        }).
        success(function(data, status, headers, config) {
          $.unblockUI();
          if (data.length == 0) {
            $scope.NoProfile = true;
            $.jStorage.set("PersonalInfo", null);
          } else {
            data = data[0];
            $("#PerFirstname").val(data.fName);
            $("#PerLastname").val(data.lName);
            $("#PerEmail").val(data.email);
            $("#PerPhNum").val(data.phone);
            $("#PerBG").val(data.bloodGroup);
            $("#PerAge").val(data.age);
            $("#perSex").val(data.sex);
            $("#PerAddress").val(data.address);

          }
        }).error(function(data, status, headers, config) {
          $.unblockUI();
        });

      }
    } catch (e) {
      alert(e)
    }
  }


  var checkMedicalInfo = function() {
    try { //alert(JSON.stringify(window.localStorage.getItem("PersonalInfo")));
      if ($.jStorage.get("MedicalInfo") == null) {

      } else {
        var MedicalData = $.jStorage.get("MedicalInfo");
        $("#PerAllergy").val(MedicalData.Allergy)
        $("#PerVaccines").val(MedicalData.Medication)
        $("#PerMedication").val(MedicalData.Vaccines)
        $("#PerMedicalNotes").val(MedicalData.Notes)
      }
    } catch (e) {
      alert(e)
    }

  };

  var checkAddInfo = function() {
    try { //alert(JSON.stringify(window.localStorage.getItem("PersonalInfo")));
      if ($.jStorage.get("AddInfo") == null) {

      } else {
        var AddInfo = $.jStorage.get("AddInfo");
        $scope.smoking = AddInfo.Smoking;
        $scope.alcohol = AddInfo.Alcohol;
        $scope.diabetic = AddInfo.Diabetes;
        $scope.disability = AddInfo.Handicap;

      }
    } catch (e) {
      alert(e);
    }

  };


  $scope.SavePersonalInfo = function() {

    try {
      //if ($.jStorage.get("deviceID") == null || $.jStorage.get("deviceID") == undefined) {
      if (true) {
        $.blockUI({
          message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
        });
