function pageprocessorTermsCondition(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  setTimeout(function() {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    //  alert($(window).height());
    var height = $(window).height() - $(".navbar-absolute-top").height() - $("#btnAgree").height() - $(".appbodycontent").height() - 60;
    $("#scroll").css('height', height);


    $scope.height = function() {
      $("#appContent").html('');
      var data1 = $compile($(pagedef.datatemplate))($scope);
      $("#appContent").append(data1);
      var height = $(window).height() - $(".navbar-absolute-top").height() - $("#btnAgree").height() - $(".appbodycontent").height() - 60;
      $("#scroll").css('height', height);

    }

    var handleEmergencyOrientation = function() {
      if (orientation == 0) {
        setTimeout(function() {
          $scope.height();
        }, 100);
      } else if (orientation == 90) {
        setTimeout(function() {
          $scope.height();
        }, 100);
      } else if (orientation == -90) {
        setTimeout(function() {
          $scope.height();
        }, 100);
      } else if (orientation == 180) {
        setTimeout(function() {
          $scope.height();
        }, 100);
      } else {}
    }
    window.handleOrientation = handleEmergencyOrientation;
    window.addEventListener('orientationchange', handleOrientation, false);

    $scope.$on('$destroy', function() {
      window.removeEventListener('orientationchange', handleEmergencyOrientation, false);
      window.handleOrientation = null;
    });


    $scope.terms = function() {
      $.jStorage.set('termsCondition', true);
      $location.path("/app/BlueLightEmergency/BlueLightEmergency");

    }


  },10);

}

