            $.each($rootScope.CoursesList.ROW, function (i, v) {

                if (v['TERM_NAME'] === $scope.selterm) {
                    if (subjcd.indexOf("$" + v.SUBJECT_CODE + '-' + v.SUBJECT_DESC + "$") === -1) {
                        subjects.push({
                            "SUBJECT_CODE": v.SUBJECT_CODE,
                            "SUBJECT_DESC": v.SUBJECT_CODE + '-' + v.SUBJECT_DESC
                        });
                        subjcd += "$" + v.SUBJECT_CODE + '-' + v.SUBJECT_DESC + "$";
                    }
                }
            });
        }
        $rootScope.subjects = subjects;
        $scope.subject = '';
        $rootScope.depts = [];
        $scope.dept = '';
        $rootScope.locations = [];
        $scope.loc = '';
    };

    $scope.selectSubjectChange = function (subject) {
        var deptcd = '';
        var depts = [];

        if (subject) {
            $.each($rootScope.CoursesList.ROW, function (i, v) {

                if (v['TERM_NAME'] === $scope.selterm && v['SUBJECT_CODE'] === $scope.subject) {
                    if (deptcd.indexOf("$" + v.SUBJECT_CODE + ' ' + v.COURSE_NUMBER + '-' + v.COURSE_TITLE + "$") === -1) {
                        depts.push({
                            "COURSE_NUMBER": v.COURSE_NUMBER,
                            "CATALOG_TITLE": v.COURSE_TITLE,
                            "CATALOG_NAME": v.SUBJECT_CODE + ' ' + v.COURSE_NUMBER + '-' + v.COURSE_TITLE
                        });
                        deptcd += "$" + v.SUBJECT_CODE + ' ' + v.COURSE_NUMBER + '-' + v.COURSE_TITLE + "$";
                    }
                }
            });
        }

        $rootScope.depts = depts;
        $scope.dept = '';
        $rootScope.locations = [];
        $scope.loc = '';
    };
    $scope.selectCourseChange = function (course) {
        var loccd = '';
        var locs = [];

        if (course) {
            $.each($rootScope.CoursesList.ROW, function (i, v) {

                if (v['TERM_NAME'] === $scope.selterm && v['SUBJECT_CODE'] === $scope.subject && v['COURSE_NUMBER'] === $scope.dept) {
                    if (loccd.indexOf("$" + v.CAMPUS + "$") === -1) {
                        locs.push({
                            "CAMPUS": v.CAMPUS
                        });
                        loccd += "$" + v.CAMPUS + "$";
                    }
                }
            });
        }
        $rootScope.locations = locs;
        $scope.loc = '';

    };

}

