   }

  functionsort_by(field,reverse,primer) {
varkey = primer ?
function(x) {
returnprimer(x[field]);
} :function(x) {
returnx[field];
};

reverse = reverse ? -1:1;

return function(a,b) {
returna = key(a),b = key(b),reverse * ((a > b) - (b > a));
};
}
$scope.CoursesResult= $scope.CoursesResult.sort(sort_by('sortfield',false,null));

}

